import React, { Component } from 'react'
import { Link, withRouter } from 'react-router-dom'
import { Menu } from "@material-ui/icons"
import ArrowBackIcon from '@material-ui/icons/ArrowBack';
import { makeStyles } from '@material-ui/core/styles';
import { AppBar, Toolbar, IconButton, Drawer, Divider} from "@material-ui/core"
import { Navbar, Nav } from "react-bootstrap";
import { loggedout } from './UserFunctions'

function TopNavBar() {

    const [ open, setOpen ] = useState(false);
    console.log(open);
    const handleDrawer = () => {
        setOpen(true);  
    }
    const handleDrawerClose = () => {
        setOpen(false);
    }

    function logout(e) {
        e.preventDefault()
        localStorage.removeItem('usertoken')
        loggedout(localStorage.getItem('user')).then(res => {
        })
        this.props.history.push('/')
    }
    const loginRegLink = (
        <ul className="navbar-nav pull-left">
            <li className="nav-item">
                <Link to="/login" className="nav-link">
                    Login
            </Link>
            </li>
            <li className="nav-item">
                <Link to="/register" className="nav-link">
                    Register
            </Link>
            </li>
        </ul>
    )
    const userLink = (
        <ul className="navbar-nav pull-right">
            <li className="nav-item">
                <Link to="/profile" className="nav-link">
                    Login
            </Link>
            </li>
            <li className="nav-item">
                <a href onClick={this.logout.bind(this)} className="nav-link">
                    Logout
                </a>
            </li>
        </ul>
    )
    return (

            <Navbar bg="dark" variant="dark">
                <IconButton onClick={handleDrawer} color="inherit" edge="start" aria-label="menu">
                    <Menu />
                </IconButton>
                <Navbar.Brand href="#home">Dal LMS</Navbar.Brand>
                <Nav className="mr-auto">
                    <Link to="/" className="nav-link">Home</Link>
                </Nav>
                <Nav>
                    {localStorage.usertoken ? userLink : loginRegLink}
                </Nav>
            </Navbar>
            // <nav className="navbar navbar-expand-lg navbar-dark bg-dark rounded">
            //     <button className="navbar-toggler"
            //         type="button"
            //         data-toggle="collapse"
            //         data-target="#navbar1"
            //         aria-controls="navbar1"
            //         aria-expanded="false"
            //         aria-label="Toggle navigation">
            //         <span className="navbar-toggle-icon"></span>
            //     </button>
            //     <div className="collapse navbar-collapse justify-content md-center"
            //         id="navbar1">
            //         <ul className="navbar-nav">
            //             <li className="navbar-nav">
            //                 <Link to="/" className="nav-link">
            //                     Home
            //         </Link>
            //             </li>
            //         </ul>

            //     </div>
            // </nav>
        )
}

export default withRouter(TopNavBar)
